import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom';
import './ContactBtn.css'

function ContactBtn() {
    const [click, setClick] = useState(true);

    const handleClick = () => setClick(!click);
    const hideContact = () => {
        setClick(true);
    }

    useEffect(() => {
        hideContact()
    }, []);

    return (
        <>
            <div className={click ? 'contact-btn' : 'contact-btn close-btn'} onClick={handleClick}>
                {click ? 'Kontakt' : 'Zamknij'}
            </div>
            <div className={click ? 'contact-details' : 'contact-details active'}>
                Mariusz Kud≈Çacz<br/>
                mariusz.kudlacz(a)op.pl
                <br/>
                <br/>
                <a href=''>Messenger</a>, <a href='https://www.linkedin.com/in/mariusz-kud%C5%82acz-00090b1b7/'>LinkedIn</a>
            </div>
        </>
    )
}

export default ContactBtn
